mapper.select:
SELECT a.article_id AS articleId, a.article_tag_id AS articleTag, u.user_name AS userName, u.user_phone AS userPhone, u.user_pic as userPic, a.article_title AS articleTitle, a.article_brief As articleBrief, a.is_stick as isStick, at.article_tag as articleTagName, a. status, DATE_FORMAT( a.create_time, '%Y-%m-%d %H:%i' ) AS createTime FROM article a LEFT JOIN user_account u ON a.user_id = u.user_uuid LEFT JOIN article_tag at on a.article_tag_id = at.article_tag_id WHERE IF (u.user_phone != #{user.userPhone},a.article_tag_id !=1,a.article_tag_id is not NULL) and a.status != '2000'
AND a.article_id = #{article.articleId}
AND u.user_phone = #{user.userPhone}
AND a.article_tag_id = #{article.articleTagId}
AND a.article_title LIKE CONCAT(CONCAT('%', #{article.articleTitle}),'%')
order by a.is_stick desc, a.create_time desc

SELECT a.article_id AS articleId, a.article_title AS articleTitle FROM article a LEFT JOIN user_account u ON a.user_id = u.user_uuid where a.article_tag_id !=1
AND u.user_phone = #{user.userPhone}
order by a.update_time desc LIMIT 0, 10
